import React, { useState } from "react";
import { Col, Button, Form, FormGroup, Label, Input } from "reactstrap";
import "../../styles/house-item.css";

const HouseItem = (props) => {
  const { item, onUpdate, onDelete } = props;
  const { id, name, imgUrl, rating, price, description } = item;

  // State for form inputs
  const [formData, setFormData] = useState({
    name: name,
    imgUrl: imgUrl,
    rating: rating,
    price: price,
    description: description,
  });

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Handle form submission for updating
  const handleSubmit = (e) => {
    e.preventDefault();
    onUpdate(id, formData); // Assume onUpdate is a prop function to handle update
  };

  // Handle delete operation
  const handleDelete = () => {
    onDelete(id); // Assume onDelete is a prop function to handle delete
  };

  return (
    <Col lg="4" md="6" sm="12" className="mb-4">
      <div className="house-item">
        <div className="house-img">
          <img src={imgUrl} alt={name} className="img-fluid" />
        </div>

        <div className="house-item-content mt-3">
          <h5 className="house-name">{name}</h5>
          <div className="house-rating">
            {[...Array(Math.floor(rating))].map((_, index) => (
              <i key={index} className="ri-star-s-fill"></i>
            ))}
            {rating % 1 !== 0 && <i className="ri-star-s-half-fill"></i>}
          </div>
          <p className="house-description">{description}</p>
          <h6 className="house-price">
            sh {price}.00 <span>/ Day</span>
          </h6>

          {/* Update form */}
          <Form onSubmit={handleSubmit} className="update-form">
            <FormGroup>
              <Label for={`name-${id}`}>Name</Label>
              <Input
                type="text"
                name="name"
                id={`name-${id}`}
                value={formData.name}
                onChange={handleChange}
                className="form-control"
              />
            </FormGroup>
            <FormGroup>
              <Label for={`imgUrl-${id}`}>Image URL</Label>
              <Input
                type="text"
                name="imgUrl"
                id={`imgUrl-${id}`}
                value={formData.imgUrl}
                onChange={handleChange}
                className="form-control"
              />
            </FormGroup>
            <FormGroup>
              <Label for={`rating-${id}`}>Rating</Label>
              <Input
                type="number"
                name="rating"
                id={`rating-${id}`}
                value={formData.rating}
                onChange={handleChange}
                className="form-control"
              />
            </FormGroup>
            <FormGroup>
              <Label for={`price-${id}`}>Price</Label>
              <Input
                type="number"
                name="price"
                id={`price-${id}`}
                value={formData.price}
                onChange={handleChange}
                className="form-control"
              />
            </FormGroup>
            <FormGroup>
              <Label for={`description-${id}`}>Description</Label>
              <Input
                type="textarea"
                name="description"
                id={`description-${id}`}
                value={formData.description}
                onChange={handleChange}
                className="form-control"
              />
            </FormGroup>
            <Button type="submit" color="primary" className="mr-2">
              Update
            </Button>
            <Button type="button" color="danger" onClick={handleDelete}>
              Delete
            </Button>
          </Form>
        </div>
      </div>
    </Col>
  );
};

export default HouseItem;
