import React, { useState } from 'react';
import { Container, Form, FormGroup, Label, Input, Button, Alert } from 'reactstrap'; 

const AddHouseForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    type: '',
    location: '',
    image: '',
    description: '',
    yearOfBirth: '',
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = validateForm(formData);
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
    } else {
      console.log('Form submitted:', formData);
    }
  };

  const validateForm = (data) => {
    let errors = {};
    if (!data.name) {
      errors.name = 'Name is required';
    }
    if (!data.type) {
      errors.type = 'Type is required';
    }
    if (!data.location) {
      errors.location = 'Location is required';
    }
    if (!data.image) {
      errors.image = 'Image URL is required';
    }
    if (!data.description) {
      errors.description = 'Description is required';
    }
    if (!data.yearOfBirth) {
      errors.yearOfBirth = 'Year of Birth is required';
    }
    return errors;
  };

  return (
    <Container>
      <h2 className="mt-4 mb-4">Add House Form</h2>
      <Form onSubmit={handleSubmit}>
        <FormGroup>
          <Label for="name">Name</Label>
          <Input
            type="text"
            id="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Enter name"
          />
          {errors.name && <Alert color="danger">{errors.name}</Alert>}
        </FormGroup>
        <FormGroup>
          <Label for="type">Type</Label>
          <Input
            type="text"
            id="type"
            value={formData.type}
            onChange={handleChange}
            placeholder="Enter type"
          />
          {errors.type && <Alert color="danger">{errors.type}</Alert>}
        </FormGroup>
        <FormGroup>
          <Label for="location">Location</Label>
          <Input
            type="text"
            id="location"
            value={formData.location}
            onChange={handleChange}
            placeholder="Enter location"
          />
          {errors.location && <Alert color="danger">{errors.location}</Alert>}
        </FormGroup>
        <FormGroup>
          <Label for="image">Image URL</Label>
          <Input
            type="text"
            id="image"
            value={formData.image}
            onChange={handleChange}
            placeholder="Enter image URL"
          />
          {errors.image && <Alert color="danger">{errors.image}</Alert>}
        </FormGroup>
        <FormGroup>
          <Label for="description">Description</Label>
          <Input
            type="textarea"
            id="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="Enter description"
          />
          {errors.description && <Alert color="danger">{errors.description}</Alert>}
        </FormGroup>
        <FormGroup>
          <Label for="yearOfBirth">Year of Birth</Label>
          <Input
            type="text"
            id="yearOfBirth"
            value={formData.yearOfBirth}
            onChange={handleChange}
            placeholder="Enter year of birth"
          />
          {errors.yearOfBirth && <Alert color="danger">{errors.yearOfBirth}</Alert>}
        </FormGroup>
        <Button color="primary" type="submit">Add House</Button>
      </Form>
    </Container>
  );
};

export default AddHouseForm;
