import React, { useState } from "react";
import { Col, Button, Form, FormGroup, Label, Input } from "reactstrap";
import "../../styles/house-item.css";

const HouseItem = (props) => {
  const { item, onUpdate, onDelete } = props;
  const { id, name, imgUrl, rating, price, description } = item;

  // State for form inputs
  const [formData, setFormData] = useState({
    name: name,
    imgUrl: imgUrl,
    rating: rating,
    price: price,
    description: description
  });

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Handle form submission for updating
  const handleSubmit = (e) => {
    e.preventDefault();
    onUpdate(id, formData); // Assume onUpdate is a prop function to handle update
  };

  // Handle delete operation
  const handleDelete = () => {
    onDelete(id); // Assume onDelete is a prop function to handle delete
  };

  return (
    <Col lg="4" md="4" sm="6" className="mb-5">
      <div className="house__item">
        <div className="house__img">
          <img src={imgUrl} alt={name} style={{ width: "100%", height: "280px" }} />
        </div>

        <div className="house__item-content mt-4">
          <h6 className="rent__price text-center mt-2">
            sh {price}.00 <span>/ Day</span>
          </h6>
          <p className="text-center mb-0">{name}</p>
          <div className="text-center">
            {[...Array(Math.floor(rating))].map((_, index) => (
              <i key={index} className="ri-star-s-fill"></i>
            ))}
            {rating % 1 !== 0 && (
              <i className="ri-star-s-half-fill"></i>
            )}
          </div>
          <p className="text-center mt-2">{description}</p>

          {/* Update form */}
          <Form onSubmit={handleSubmit}>
            <FormGroup>
              <Label for="name">Name</Label>
              <Input type="text" name="name" id="name" value={formData.name} onChange={handleChange} />
            </FormGroup>
            <FormGroup>
              <Label for="imgUrl">Image URL</Label>
              <Input type="text" name="imgUrl" id="imgUrl" value={formData.imgUrl} onChange={handleChange} />
            </FormGroup>
            <FormGroup>
              <Label for="rating">Rating</Label>
              <Input type="number" name="rating" id="rating" value={formData.rating} onChange={handleChange} />
            </FormGroup>
            <FormGroup>
              <Label for="price">Price</Label>
              <Input type="number" name="price" id="price" value={formData.price} onChange={handleChange} />
            </FormGroup>
            <FormGroup>
              <Label for="description">Description</Label>
              <Input type="textarea" name="description" id="description" value={formData.description} onChange={handleChange} />
            </FormGroup>
            <Button type="submit" color="primary">Update</Button>
            <Button color="danger" className="ml-2" onClick={handleDelete}>Delete</Button>
          </Form>
        </div>
      </div>
    </Col>
  );
};

export default HouseItem;
