import React, { useEffect } from "react";
import Helmet from "../components/Helmet/Helmet";
import carData from "../assets/data/carData";
import { Container, Row, Col } from "reactstrap";
import { useParams } from "react-router-dom";

const CarDetails = () => {
  const { slug } = useParams();

  const singleCarItem = carData.find((item) => item.carName === slug);

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [singleCarItem]);

  return (
    <Helmet title={singleCarItem ? singleCarItem.carName : "Car Details"}>
      <section>
        <Container>
          {singleCarItem && (
            <Row>
              <Col lg="6">
                <img src={singleCarItem.imgUrl} alt={singleCarItem.carName} className="w-100" />
              </Col>

              <Col lg="6">
                <div className="car__info">
                  <h2 className="section__title">{singleCarItem.carName}</h2>

                  <div className="d-flex align-items-center gap-5 mb-4 mt-3">
                    <h6 className="rent__price fw-bold fs-4">
                      sh{singleCarItem.price}.00 / Day
                    </h6>

                    <span className="d-flex align-items-center gap-2">
                      <span style={{ color: "#f9a826" }}>
                        {[...Array(singleCarItem.rating)].map((_, index) => (
                          <i key={index} className="ri-star-s-fill"></i>
                        ))}
                      </span>
                      ({singleCarItem.rating} ratings)
                    </span>
                  </div>

                  <p className="section__description">
                    {singleCarItem.description}
                  </p>
                </div>
              </Col>
            </Row>
          )}
        </Container>
      </section>
    </Helmet>
  );
};

export default CarDetails;
