import React, { useState } from "react";
import { Col, Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import "../../styles/house-item.css";

const HouseItem = (props) => {
  const { item, onUpdate, onDelete } = props;
  const { id, name, imgUrl, rating, price, description } = item;

  // State for edit modal
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);

  // State for form inputs
  const [formData, setFormData] = useState({
    name: name,
    imgUrl: imgUrl,
    rating: rating,
    price: price,
    description: description,
  });

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Handle form submission for updating
  const handleSubmit = (e) => {
    e.preventDefault();
    onUpdate(id, formData); // Assume onUpdate is a prop function to handle update
    toggle(); // Close modal after update
  };

  // Handle delete operation
  const handleDelete = () => {
    onDelete(id); // Assume onDelete is a prop function to handle delete
  };

  return (
    <Col lg="4" md="6" sm="12" className="mb-4">
      <div className="house-item">
        <div className="house-img">
          <img src={imgUrl} alt={name} className="img-fluid" />
        </div>

        <div className="house-item-content mt-3">
          <h5 className="house-name">{name}</h5>
          <div className="house-rating">
            {[...Array(Math.floor(rating))].map((_, index) => (
              <i key={index} className="ri-star-s-fill"></i>
            ))}
            {rating % 1 !== 0 && <i className="ri-star-s-half-fill"></i>}
          </div>
          <p className="house-description">{description}</p>
          <h6 className="house-price">
            sh {price}.00 <span>/ Day</span>
          </h6>

          <div className="text-center mt-3">
            <Button color="primary" onClick={toggle}>
              Edit
            </Button>{" "}
            <Button color="danger" onClick={handleDelete}>
              Delete
            </Button>
          </div>

          {/* Modal for editing */}
          <Modal isOpen={modal} toggle={toggle}>
            <ModalHeader toggle={toggle}>Edit House Item</ModalHeader>
            <ModalBody>
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <label htmlFor={`name-${id}`}>Name</label>
                  <input
                    type="text"
                    className="form-control"
                    id={`name-${id}`}
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor={`imgUrl-${id}`}>Image URL</label>
                  <input
                    type="text"
                    className="form-control"
                    id={`imgUrl-${id}`}
                    name="imgUrl"
                    value={formData.imgUrl}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor={`rating-${id}`}>Rating</label>
                  <input
                    type="number"
                    className="form-control"
                    id={`rating-${id}`}
                    name="rating"
                    value={formData.rating}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor={`price-${id}`}>Price</label>
                  <input
                    type="number"
                    className="form-control"
                    id={`price-${id}`}
                    name="price"
                    value={formData.price}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor={`description-${id}`}>Description</label>
                  <textarea
                    className="form-control"
                    id={`description-${id}`}
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                  />
                </div>
                <ModalFooter>
                  <Button color="primary" type="submit">
                    Save Changes
                  </Button>{" "}
                  <Button color="secondary" onClick={toggle}>
                    Cancel
                  </Button>
                </ModalFooter>
              </form>
            </ModalBody>
          </Modal>
        </div>
      </div>
    </Col>
  );
};

export default HouseItem;
