import React from "react";
import Helmet from "../components/Helmet/Helmet";
import { Container, Row, Col } from "reactstrap";
import AboutSection from "../components/UI/AboutSection";
import ServicesList from "../components/UI/ServicesList";
import HouseData from "../assets/data/HouseData";
import HouseItem from "../components/UI/HouseItem"; 
import Testimonial from "../components/UI/Testimonial";

const Home = () => {
  // Assuming ServicesList returns an array of items
  const services = ServicesList(); // Assuming this function or component returns an array of items

  // Splitting services into two rows
  const firstRowItems = services.slice(0, Math.ceil(services.length / 2));
  const secondRowItems = services.slice(Math.ceil(services.length / 2), services.length);

  return (
    <Helmet title="Home">
      <section>
        <Container>
          <Row>
            <Col lg="12" className="mb-5 text-center">
              <h6 className="section__subtitle">See our</h6>
              <h2 className="section__title">Popular Services</h2>
            </Col>
          </Row>
          {/* First Row of Services */}
          <Row>
            {firstRowItems.map((item) => (
              <Col key={item.id} lg="6" md="6" sm="12" className="mb-4">
                <HouseItem item={item} />
              </Col>
            ))}
          </Row>
          {/* Second Row of Services */}
          <Row>
            {secondRowItems.map((item) => (
              <Col key={item.id} lg="6" md="6" sm="12" className="mb-4">
                <HouseItem item={item} />
              </Col>
            ))}
          </Row>
        </Container>
      </section>

      <section>
        <Container>
          <Row>
            <Col lg="12" className="text-center mb-5">
              <h6 className="section__subtitle">Come with</h6>
              <h2 className="section__title">Hot Offers</h2>
            </Col>
          </Row>
          <Row>
            {HouseData.slice(0, 6).map((item) => (
              <HouseItem item={item} key={item.id} />
            ))}
          </Row>
        </Container>
      </section>

      <section>
        <Container>
          <Row>
            <Col lg="12" className="mb-4 text-center">
              <h6 className="section__subtitle">Our clients say</h6>
              <h2 className="section__title">Testimonials</h2>
            </Col>
          </Row>
          <Testimonial />
        </Container>
      </section>
    </Helmet>
  );
};

export default Home;
